import React, { Component } from 'react';
import './TodoList.css';
import TodoItems from './Components/TodoItems';
import DenseAppBar from './Components/DenseAppBar';
import InteractiveList from './Components/InteractiveList';
import NewList from './Components/NewList';
import Tasks from './Components/Tasks';
import Options from './Components/Options';

var tasks = "";
class TodoList extends Component {
constructor(props) {
super(props);

this.state = {
  show: false,
  showMenu: false,
  showNewList: false,
 items:[
     {
       list: "Personal",
       DeadLines: { Today: [], Tommorrow: [], Someday: []}
     },
     {
       list: "Work",
       DeadLines: { Today: [], Tommorrow: [], Someday: []}
     },
     {
       list: "GroceryList",
       DeadLines: { Today: [], Tommorrow: [], Someday: []}
     }
 ]
};

this.style = {
  padding: "10px",
  width: "165px",
  border: "2px solid #FFF",
  margin: "10px"
}

this.addItem=this.addItem.bind(this);
this.displayItem=this.displayItem.bind(this);
}

addItem(e){
     if(this._inputElement.value !== ""){
       var DeadLines= { Today: [], Tommorrow: [], Someday: []};
      var newItem = {
        list: this._inputElement.value,
        DeadLines: DeadLines,
        key: Date.now()
      };

      this.setState((prevState) => {
        return {
          items: prevState.items.concat(newItem)
        };
      });
    }

    this._inputElement.value = "";
    e.preventDefault();
}

displayItem(list){
  tasks=this.state.items;
  this.setState({show: true });
}

toggleMenuHandler = () => {
  const doesShow = this.state.showMenu;
  this.setState({showMenu: !doesShow});
}

showListHandler = () => {
    this.setState({ showNewList: true });
  };

AddTaskHandler = (day, task) => {
      this.setState({ showNewList: false });

      var items = this.state.items.slice();
    if (task !== "") {
      if (listIndex === "") {
        items[0].DeadLines[day].push(task);
      } else {
        items[listIndex].DeadLines[day].push(task);
      }
    }
    this.setState({
      myLists: myLists
    });

    };

  render() {

    let menu = null;
    let content = null;
          menu = (
            <div>
                <form onSubmit={this.addItem}>
                  <input style={this.style} ref={(a) => this._inputElement =a}
                  placeholder="enter List">
                  </input>
                </form>
                <TodoItems entries={this.state.items}
                           display={this.displayItem}/>

             </div>);

         if(this.state.show){
           content = 
           <div>
            <Tasks
            taskName={tasks["list"]}
            />
          </div>

         }

    return (
      <div className="TodoList">
        <div className="top">
        <DenseAppBar click={() =>this.toggleMenuHandler()}/>
        </div>
        <div className="row">
          <div className={this.state.showMenu?'left show-menu':'left hide-menu'}>
          <div className="header">
          {menu}
          </div>
          </div>
          <div className={this.state.showMenu?'right show-hmenu':'right show-fmenu'}>
          <button onClick={()=>this.showListHandler()}>New</button>
          <NewList showNewList={this.state.showNewList} handleAddTask={this.AddTaskHandler}>
          <input placeholder="I Want to.."/>
          <Options entries={this.state.items}/>
        </NewList>
        {content}
          </div>
        </div>
      </div>
    );
  }
}

export default TodoList;
