import React,{Component} from 'react';

import Options1 from './Options1';
import Options2 from './Options2';
import "./NewList.css";

let day = "";
class NewList extends Component {


  param1 = (pa) => {
    var items = this.state.items.slice();
    for(var i=0;i<items.length;i++){
      if(items[i].list == pa){
        console.log(items[i].DeadLines);
        items[i].DeadLines[day].push(this._inputElement2.value);
      }
    }

    this.setState({
items: items
    });




console.log(this.state.items);
  }

  param2 = (p) => {
    day=p;
   }

  render() {

    let showHideClassName;
    if (this.props.showNewList) {
      showHideClassName = "modal display-block";
    } else {
      showHideClassName = "modal display-none";
    }
    return (
      <div className={showHideClassName}>
        <section className="modal-main">
        <input placeholder="I Want to.." ref={(b) => this._inputElement2 =b}/>
          <div className="modal-sidebar">
          <Options2 param2={this.param2}/>
            <div className="SelectList">


          <Options1 entries={this.items} param1={this.param1}/>
            </div>
          </div>
          <div className="modal-footer">
            <button className="AddTask" onClick={this.handleAddTask}>
              <strong>Add Task</strong>
            </button>
          </div>
        </section>
      </div>
    );
  }
}


export default NewList;
