import React, { Component } from 'react';
import './TodoList.css';
import TodoItems from './Components/TodoItems';
import DenseAppBar from './Components/DenseAppBar';
import InteractiveList from './Components/InteractiveList';
import NewList from './Components/NewList';

class TodoList extends Component {
constructor(props) {
super(props);

this.state = {
  showMenu: false,
  showNewList: false,
 items:[]
};

this.style = {
  padding: "10px",
  width: "165px",
  border: "2px solid #FFF",
  margin: "10px"
}

this.addItem=this.addItem.bind(this);
this.deleteItem=this.deleteItem.bind(this);
}

addItem(e){
     if(this._inputElement.value !== ""){
      var newItem = {
        text: this._inputElement.value,
        key: Date.now()
      };

      this.setState((prevState) => {
        return {
          items: prevState.items.concat(newItem)
        };
      });
    }

    this._inputElement.value = "";
    e.preventDefault();
}

deleteItem(key){
  var filteredItems = this.state.items.filter(function (item){
    return (item.key !== key)
  });
this.setState({
  items: filteredItems
});

}

toggleMenuHandler = () => {
  const doesShow = this.state.showMenu;
  this.setState({showMenu: !doesShow});
}

showListHandler = () => {
    this.setState({ showNewList: true });
  };

  hideListHandler = () => {
      this.setState({ showNewList: false });
    };

  render() {

    let menu = null;


          menu = (
            <div>
                <form onSubmit={this.addItem}>
                  <input style={this.style} ref={(a) => this._inputElement =a}
                  placeholder="enter List">
                  </input>
                </form>
                <TodoItems entries={this.state.items}
                           delete={this.deleteItem}/>

             </div>);

    return (
      <div className="TodoList">
        <div className="top">
        <DenseAppBar click={() =>this.toggleMenuHandler()}/>
        </div>
        <div className="row">
          <div className={this.state.showMenu?'left show-menu':'left hide-menu'}>
          <div className="header">
          {menu}
          </div>
          </div>
          <div className={this.state.showMenu?'right show-hmenu':'right show-fmenu'}>
          <button onClick={()=>this.showListHandler()}>New</button>
          <NewList showNewList={this.state.showNewList} handleClose={this.hideListHandler}>
          <input placeholder="I Want to.."/>
          <p>Data</p>
        </NewList>

          </div>
        </div>
      </div>
    );
  }
}

export default TodoList;
