import React, { Component } from "react";
import DisplayTasks from './DisplayTasks';
import "./Tasks.css";
var tasks=[];
class Tasks extends Component {
  constructor()
  {
    super()
    {
     this.state={
       task:"",
       show:false
     }
    }
  }
  display(name){
  var show=!this.state.show;
  var task="";
   let items=this.props.items;
   if(show)
   {
   for(let i=0;i<items.length;i++)
   {
     if(items[i].key===this.props.currentKey)
     {
      tasks=items[i].DeadLines[name];
      console.log(items[i].DeadLines[name]);
     }
   }
   for(var i=0;i<tasks.length;i++)
   {
     task=task+tasks[i];
     task=task+'\n';
   }}
   console.log(task);
   this.setState(
   {
     show:show,
     task :task
   });

  }

  render() {

    return (
      <div className="Tasks">
           <p>{this.props.taskName}</p>
          <div className="Card">
            <div className="Task" onClick={this.display.bind(this,"Today")}>
            Today  
            </div>
            <div className="Task" onClick={this.display.bind(this,"Tomorrow")}>
            Tomorrow
            </div>
            <div className="Task" onClick={this.display.bind(this,"Someday")}>
            Someday
            </div>
          </div>
          <div className="DisplayTasks">
           {this.state.task}
           </div>

      </div>
    );
  }
}

export default Tasks;
