import React from 'react';
import './NewList.css';

const NewList = ({ handleAddTask, showNewList, children }) => {
  const showHideClassName = showNewList ? "modal display-block" : "modal display-none";

  return (
    <div className={showHideClassName}>
      <section className="modal-main">
        {children}
        <button onClick={handleAddTask}>AddTask</button>
      </section>
    </div>
  );
};

export default NewList;
